div id="HEADLINE162" class='ladi-element'><h3 class='ladi-headline ladi-transition'>Quý khách hàng vui lòng điền đầy đủ thông tin, chúng tôi sẽ liên hệ với quý khách hàng chỉ sau ít phút.<br></h3></div><div id="FORM5" data-config-id="64d711a7f4a46000121a9504" class='ladi-element'>

<form id="signup_form1">
	<div class="mb-3">
		<label for="exampleInputEmail1" class="form-label">Họ và Tên</label>
		<input type="text" class="form-control" name="name1" id="name1">
	</div>
	<div class="mb-3">
		<label for="exampleInputPassword1" class="form-label">Số điện thoại</label>
		<input type="text" class="form-control" name="phone_number1" id="phone_number1" >
	</div>
	<div class="mb-3">
		<label for="exampleInputPassword1" class="form-label">Địa chỉ</label>
		<input type="text" class="form-control" name="your_addrest1" id="your_addrest1" >
	</div>
	<div class="mb-3">
		<label for="exampleInputPassword1" class="form-label">Nhắn cho chúng tôi</label>
		<textarea class="form-control" name="your_message1" id="your_message1" placeholder="Để lại lời nhắn cho chúng tôi"></textarea>
	</div>
	<div class="d-grid gap-2 col-6 mx-auto">
		<button type="submit" class="btn btn-primary">Đăng ký</button>
	</div>
</form>
<script>
	jQuery(document).ready(function () {
		jQuery("#signup_form1").submit(function(event) {
			event.preventDefault();
			var name = jQuery("#name1").val();
			if(name == ''){
				alert('Tên không được để trống');
				return;
			}
			/*var email = jQuery("#email").val();
			if(email == ''){
				alert('Email không được để trống');
				return;
			}*/
			var phone_number = jQuery("#phone_number1").val();
			if(phone_number == ''){
				alert('Số điện thoại không được để trống');
				return;
			}
			var phoneReg = /(((\+|)84)|0)(3|5|7|8|9)+([0-9]{8})\b/;
			if( !phoneReg.test( phone_number ) ) {
				alert('Số điện thoại không đúng định dạng');
				return;
			}

			/*var emailReg = /^([\w\.\-]+@([\w\-]+\.)+[\w]{2,4})?$/;
			if( !emailReg.test( email ) ) {
				alert('Email không đúng định dạng');
				return;
			}*/
			var formData = {
				name: jQuery("#name1").val(),
				//email: jQuery("#email").val(),
				phone_number: jQuery("#phone_number1").val(),
				your_addrest: jQuery("#your_addrest1").val(),
				your_message: jQuery("#your_message1").val(),
				//g_recaptcha: grecaptcha.getResponse(),
			};
			var domain = window.location.origin+"/vneu";
			jQuery.ajax({
				type: "POST",
				url: domain+"/ajax/register/dang_ky_thanh_vien",
				data: formData,
				dataType: "json",
				encode: true,
			}).done(function (data) {
				if(data.err == false){
					Swal.fire({
						title: data.msg,
						icon: 'success',
						showLoaderOnConfirm: true,
					}).then((result) => {
						location.reload();
					});
				}else{
					Swal.fire({
						title: data.msg,
						icon: 'error',
						showLoaderOnConfirm: true,
					}).then((result) => {
						location.reload();
					});
				}
			});
		});
